use [AdventureWorks2017]

--======AdventureWorks DB=========select * from Person.Personselect * from Person.BusinessEntityAddress select * from Person.Address select * from Person.Person pjoin Person.BusinessEntityAddress BEA on p.BusinessEntityID=BEA.BusinessEntityIDjoin Person.Address A ON BEA.AddressID=A.AddressID ---Inner join Example to Find person PhoneNumber and Passwordselect * from Person.Person pjoin Person.Password pwd on p.BusinessEntityID=pwd.BusinessEntityIDjoin Person.PersonPhone phn on phn.BusinessEntityID=p.BusinessEntityID-----------------Lef join Example------select * from Person.Personselect * from  sales.PersonCreditCardselect * from  Sales.CreditCardselect p.FirstName,p.MiddleName,p.LastName,c.CreditCardIDfrom person.Person pleft join sales.PersonCreditCard c on p.BusinessEntityID=c.BusinessEntityIDwhere c.BusinessEntityID is nullselect *--p.FirstName,p.MiddleName,p.LastName,c.CreditCardID,cc.*from person.Person pleft join sales.PersonCreditCard c on p.BusinessEntityID=c.BusinessEntityIDleft join Sales.CreditCard cc on c.CreditCardID=cc.CreditCardIDwhere c.BusinessEntityID is null