--Stored procedures with no parametersCREATE procedure dbo.spCustomerDetailsASBEGINselect e.EmpID,e.DeptID,e.Gender,e.Age,e.PhoneNumber,d.DeptName from dbo.EMPLOYEE ejoin dbo.DEPARTMENT d on e.DeptID=d.DeptIDENDexec dbo.spCustomerDetails--Stored procedures with input parametersCREATE PROCEDURE dbo.spCustomerDetails_WithInputParameters(@EmpID INT)ASBEGINSELECT e.EmpID,e.DeptID,e.Gender,e.Age,e.PhoneNumber,d.DeptName FROM dbo.EMPLOYEE eJOIN dbo.DEPARTMENT d ON e.DeptID=d.DeptIDWHERE e.EmpID=@EmpIDENDEXEC dbo.spCustomerDetails_WithInputParameters 102--Stored procedures with Input and output parametersCREATE PROCEDURE dbo.spCustomerDetails_WithInputOutputParameters(@Gender VARCHAR(10),@EmpCount INT OUTPUT)ASBEGINSELECT @EmpCount=COUNT(EmpID) FROM dbo.EMPLOYEE e WHERE Gender=@GenderENDDECLARE @TotalEmployees int--EXEC dbo.spCustomerDetails_WithInputOutputParameters 'Male',@EmpCount=@TotalEmployees OUTEXEC dbo.spCustomerDetails_WithInputOutputParameters 'Male',@TotalEmployees OUTSELECT @TotalEmployees